function getServerListfromFile($sample_file)
{
	$json_sample = fopen($sample_file, "r") or exitwithmsg("Unable to open file!");
	$json_content = fread($json_sample,filesize($sample_file));
	fclose($json_sample);
	return $json_content;
}

function exitwithmsg($s)
{
    die('<h2>' . $s . '</h2>');
}

function getMapImage($map)
{
    if (file_exists("images/maps/" . strtolower($map) . ".jpg"))
        return "images/maps/" . strtolower($map) . ".jpg";
    else
		return "data:image/jpeg;base64,";
	}
function getIPCountry($IP)
{
    $data = json_decode(file_get_contents('http://ip-api.com/json/' . $IP), true);
    return strtolower($data["countryCode"]);
}

function getCountryFlag($CountryCode)
{
    if (file_exists("images/flags/" . strtoupper($CountryCode) . ".jpg"))
        return "images/flags/" . strtoupper($CountryCode) . ".jpg";
    else
        return "data:image/jpeg;base64,";
}

function getCountrySmallFlag($CountryCode)
{
    if (file_exists("images/flags_small/" . strtolower($CountryCode) . ".png"))
        return "images/flags_small/" . strtolower($CountryCode) . ".png";
    else
        return "data:image/jpeg;base64,";
}

function getMod($gt, $map)
{
    if ($map == "zm_tomb")
        return "Origins";
    else if ($map == "zm_highrise")
        return "Die Rise";
    else if ($map == "zm_nuked")
        return "Survival";
    else if ($map == "zm_buried")
        return "Buried";
    else if ($map == "zm_prison")
        return "Mob of the Dead";
    else if ($map == "zm_transit_dr")
        return "Turned";
    else if ($map == "zm_transit") {
        if ($gt == "zclassic")
            return "Tranzit";
        else if ($gt == "zstandard")
            return "Survival";
    } else
        return $gt;
}
function getMapName($map_name)
{
    $maps = array(
        "zm_nuked" => "NukeTown",
        "zm_tomb" => "Excavation Site 64",
        "zm_buried" => "Resolution 1295",
        "zm_transit" => "Green Run",
        "zm_highrise" => "Great Leap Forward",
        "zm_prison" => "Alcatraz Island",
        "zm_transit_dr" => "Diner"
    );
    if (isset($maps[$map_name])) {
        return $maps[$map_name];
    } else {
        return $map_name;
    }
}

function getColoredName($host)
{
    $count_rpl = 0;
    $hostname  = $host;
    $h2        = str_replace("^1", "<div style='color:#FF0000;'>", $hostname, $count_rpl_1);
    $h3        = str_replace("^2", "<div style='color:#00FF00;'>", $h2, $count_rpl_2);
    $h4        = str_replace("^3", "<div style='color:#FFFF00;'>", $h3, $count_rpl_3);
    $h5        = str_replace("^4", "<div style='color:#0000FF;'>", $h4, $count_rpl_4);
    $h6        = str_replace("^5", "<div style='color:#00FFFF;'>", $h5, $count_rpl_5);
    $h7        = str_replace("^6", "<div style='color:#FF00FF;'>", $h6, $count_rpl_6);
    $h8        = str_replace("^7", "<div style='color:#FFFFFF;'>", $h7, $count_rpl_7);
    $h9        = str_replace("^0", "<div style='color:#000000;'>", $h8, $count_rpl_8);
    $final     = str_replace("^8", "<div style='color:#000000;'>", $h9, $count_rpl_9);
    
    $count = $count_rpl_1 + $count_rpl_2 + $count_rpl_3 + $count_rpl_4 + $count_rpl_5 + $count_rpl_6 + $count_rpl_7 + $count_rpl_8 + $count_rpl_9;
    
    $final = $final . str_repeat("</div>", $count);
    return $final;
}

function getRawName($server_name)
{
    $temp  = str_replace("^0", "", $server_name);
    $temp2 = str_replace("^1", "", $temp);
    $temp3 = str_replace("^2", "", $temp2);
    $temp4 = str_replace("^3", "", $temp3);
    $temp5 = str_replace("^4", "", $temp4);
    $temp6 = str_replace("^5", "", $temp5);
    $temp7 = str_replace("^6", "", $temp6);
    $temp8 = str_replace("^7", "", $temp7);
    $temp9 = str_replace("^8", "", $temp8);
    return $temp9;
}

